import React from 'react'
import { Spring, animated } from 'react-spring/renderprops'
import { GradientPinkRed as Gradient } from '@vx/gradient'
import * as easings from 'd3-ease'


export default class Logo extends React.Component {
  state = { offset: 0 }
  path = React.createRef()
  componentDidMount() {
    this.setState({ offset: this.path.current.getTotalLength() })
  }
  render() {
    const { offset } = this.state
    return (
      <div
        style={{
          background: '#272727',
          width: '100%',
          height: '100%',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
        onClick={() => this.forceUpdate()}>
        <svg width="180" viewBox="0 0 23 23">
          <Gradient id="gradient-dashoffset" />
          <g
            fill="#373737"
            stroke="url(#gradient-dashoffset)"
            strokeWidth="0.5">
            <Spring
              native
              reset
              from={{ dash: offset }}
              to={{ dash: 0 }}
              config={{
                delay: 1000,
                duration: 3000,
                easing: easings.easeCubic,
              }}>
              {props => (
                <animated.path
                  ref={this.path}
                  strokeDasharray={offset}
                  strokeDashoffset={props.dash}
                  d="M46.75 271.45C43.05 264.94 33.07 265.95 30.83 273.05C29.64 276.88 25.12 291.23 17.3 316.1C16.54 318.53 16.8 321.16 18.03 323.38C58.94 397.3 82.55 439.95 88.84 451.32C92.81 458.47 103.8 457.2 105.9 449.35C107.69 442.63 112.18 425.85 119.37 399C80.63 330.97 56.43 288.45 46.75 271.45Z"
                />
              )}
            </Spring>
          </g>
        </svg>
      </div>
    )
  }
}
