import React, { useState } from 'react';
import { useSpring, animated, config } from 'react-spring';






const Card = (props) => {
  const [hover, setHover] = useState(false)
  const hoverState = useSpring({
    transform: hover
      ? 'translate3d(0px, -12px, 0px) scale(1.03)'
      : 'translate3d(0px, 0px, 0px) scale(1)',
    boxShadow: hover
    ? '-1px 15px 45px 0px rgba(0, 0, 0, 0.3)'
    : '-1px 10px 45px 0px rgba(0, 0, 0, 0.2)',
    config: config.wobbly
  })

  return (
    <animated.div
      style={{
        height: 'fit-content',
        width: 'fit-content',
        color: '#f7f7f7',
        marginLeft: '3%',
        fontSize: '30px',
        marginRight: '3%',
        whiteSpace: 'wrap',
        background: 'rgb(169, 169, 169, 0.2)',
        padding: '20px',
        borderRadius: '20px',
        maxWidth: '26%',
        marginBottom: '3%',

    
        ...hoverState
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
        <p>
        {props.content}
        </p>
    </animated.div>
  )
}

export default Card
