import React from 'react';
import '../css/Button.css'
import {Modal, Button, ButtonToolbar} from 'react-bootstrap'
import { Spring } from 'react-spring/renderprops'

function FileModal(props) {
    return (
        <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        >
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
            Add File
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <form id="frmUploader" enctype="multipart/form-data" action="api/Upload/" method="post">
        <input type="file" name="imgUploader" multiple />
        <input type="submit" name="submit" id="btnSubmit" value="Upload" /> </form>
        </Modal.Body>
        <Modal.Footer>
            <Button onClick={props.onHide}>Upload</Button>
        </Modal.Footer>
        </Modal>
    );
}
    
function FileUploadButton() {
    const [modalShow, setModalShow] = React.useState(false);
    
    return (
        <ButtonToolbar>
          <Spring
              config = {{delay: 0, tension: 50}}
              from={{ opacity: 0 }}
              to={{ opacity: 1 }}>
              {props => 
              <div style={props}>
                  <div className="button-div" onClick={() => setModalShow(true)}>
                          <h1 className="uploadbutton">+</h1>
                  </div>
              </div>
              }
          </Spring>  
        <FileModal
            show={modalShow}
            onHide={() => setModalShow(false)}
        />
        </ButtonToolbar>
    );
}

  
export default FileUploadButton





